,p_process_name=>'Preenche itens'
,p_process_sql_clob=>wwv_flow_string.join(wwv_flow_t_varchar2(
'BEGIN',
'    SELECT COUNT(*)',
'    INTO :P0_DELEGACOES',
'    FROM DELEGACAO;',
'',
'    SELECT COUNT(*)',
'    INTO :P0_ESPORTES',
'    FROM ESPORTE;',
'',
'    SELECT COUNT(*)',
'    INTO :P0_COMPETICOES',
'    FROM COMPETICAO;',
'',
'    SELECT count(1)',
'    INTO :P0_MEDALHAS',
'    FROM RESULTADO_COMPETICAO',
'    WHERE (ID_FASE = 5 AND COLOCACAO IN (1,2,3))',
'    OR (ID_FASE = 4 AND COLOCACAO IN (1,4));',
'',
'    SELECT count(1)',
'    INTO :P0_RESULTADOS',
'    from (SELECT count(1)',
'    FROM RESULTADO R',
'    JOIN BATERIA B ON B.ID = R.ID_BATERIA',
'    WHERE COLOCACAO IS NOT NULL',
'    GROUP BY B.ID);',
'',
'    select count(1)',
'    into :P0_RESULTADOS_COMPETICAO',
'    from (select 1',
'    from competicao c',
'    join bateria b on b.id_competicao = c.id',
'    join resultado r on r.id_bateria = b.id',
'    where r.classificacao is not null',
'    group by c.id);',
'END;'))
